// Функция сортировки элементов главной диагонали матрицы
void mainDiagonalSort(int n, double matrix[][50]) {
for (int i = 0; i < n; i++) {
for (int j = i + 1; j < n; j++) {
if (matrix[i][i] > matrix[j][j]) { // Сравниваем два элемента главной диагонали матрицы
double temp = matrix[i][i];
matrix[i][i] = matrix[j][j]; // Меняем местами элементы, если правый меньше левого
matrix[j][j] = temp;
}
}
}
}

// Функция сортировки элементов побочной диагонали матрицы
void sideDiagonalSort(int n, double matrix[][50]) {
for (int i = 0; i < n; i++) {
for (int j = 0; j < n - i - 1; j++) {
if (matrix[i][n - i - 1] > matrix[j][n - j - 1]) { // Сравниваем два элемента побочной диагонали матрицы
double temp = matrix[i][n - i - 1];
matrix[i][n - i - 1] = matrix[j][n - j - 1]; // Меняем местами элементы, если правый меньше левого
matrix[j][n - j - 1] = temp;
}
}
}
}

